import { Link } from "react-router-dom";
import { useEffect,useState } from "react";
import axios from 'axios';
const baseUrl='http://127.0.0.1:8000/api';
function TLogin(){

  const[teacherLoginData,setteacherLoginData]=useState({
    email:'',
    password:''
  });

  const handelChange=(event)=>{
    setteacherLoginData({ 
      ...teacherLoginData,
       [event.target.name]:event.target.value
   });
  };
  const submitForm=()=>{
      console.log(teacherLoginData);
  }

  useEffect(()=>{
    document.title='Teacher Register';
 });
    return(
        <div className="container">
          <div className="row">
            <div className="col-8 offset-3">
             <div className="card">
                <h5 className="card-header">Teacher Login</h5>
                  <div className="card-body">
                  <form>
  <div className="mb-3">
    <label for="exampleInputEmail1" className="form-label">Email</label>
    <input type="email" value={teacherLoginData.email} name='email' onChange={handelChange} className="form-control"/>
  </div>
  <div className="mb-3">
    <label for="exampleInputPassword1" className="form-label">Password</label>
    <input type="password" value={teacherLoginData.email} name='password'  onChange={handelChange} className="form-control" id="exampleInputPassword1"/>
  </div>
  <div className="mb-3 form-check">
    <input type="checkbox" className="form-check-input" id="exampleCheck1"/>
    <label className="form-check-label" for="exampleCheck1">Remember Me</label>
  </div>
  <button type="submit" onClick={submitForm} className="btn btn-primary">Submit</button>
</form>
                  </div>
                </div> 
             </div>
            </div>
        </div>
    );
}
export default TLogin;
