import axios from 'axios';
import TSidebar from './TSidebar';
import { useState,useEffect } from 'react';
const baseUrl='http://127.0.0.1:8000/api';
function Addcourses(){
    const [cats,setCats]= useState([]);
    const[courseData,setCourseData]=useState({
        category:'',
        title:'',
        description:'',
        f_img:'',
        techs:''
    });
    useEffect(()=>{
        try{
            axios.get(baseUrl+'/category').then((res)=>{
                //console.log(res.data);
                setCats(res.data)
             
            });
            }catch(error){
             console.log(error);
            }
         },[]);
      //   console.log(cats);

      const handelChange=(event)=>{
        setCourseData({
            courseData,
            
        })
      }
    return(
         <div className="container mt-4">
            <div className="row">
                <aside className="col-md-3">
                    <TSidebar/>
                </aside>
                <section className="col-md-9">
                    <div className="card">
                        <h5 className="card-header">Addcourses</h5>
                        <div className="card-body">
                        <div className="mb-3 row">
                             <label for="staticEmail" className="col-sm-2 col-form-label">Category</label>
                                <div className="col-sm-10">
                                  <select name='catagory' className='form-control'>
                                    {cats.map((catagory,index)=>{return<option key={index}>{catagory.title}</option>})}
                                  </select>
                                </div>
                        </div>
                        <div className="mb-3 row">
                             <label for="staticEmail" className="col-sm-2 col-form-label">Title</label>
                                <div className="col-sm-10">
                                  <input type="text" className="form-control" id="staticEmail" />
                                </div>
                        </div>
                        
                        <div className="mb-3 row">
                     <label for="inputPassword" className="col-sm-2 col-form-label">Description</label>
                       <div className="col-sm-10">
                         
                         <textarea className="form-control" id="exampleFormControlTextarea1" rows="3"></textarea>
                       </div>
                </div>
                     <div className="mb-3 row">
                             <label for="staticEmail" className="col-sm-2 col-form-label">Featured Image</label>
                                <div className="col-sm-10">
                                <input type="file" className="form-control" id="staticEmail" />
                         </div>
                     </div>
                     
                
                <div className="mb-3 row">
                     <label for="inputPassword" className="col-sm-2 col-form-label">Technologies</label>
                       <div className="col-sm-10">
                         
                         <textarea className="form-control" id="exampleFormControlTextarea1" rows="3"></textarea>
                       </div>
                </div>

                <hr/>
                    <button className="btn btn-primary">Submit</button>
                
                        </div>
                    </div>
               
                </section>
            </div>
         </div>
    );
}
export default Addcourses;
